import { useEffect, useState } from "react"

const useWeatherApi = () => {
    const ciudades = [
    { nombre: "Buenos Aires", lat: -34.6, lon: -58.4, img: 'data:image/jpeg;base64,'},
    { nombre: "Madrid", lat: 40.4167, lon: -3.7036, img: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTtPr2mhkEwsbiz3C8NbuC1uYimEziiDZ35sQ&s' },
    { nombre: "Ciudad de México", lat: 19.4326, lon: -99.1332, img: 'data:image/jpeg;base64,' },
    { nombre: "Tokio", lat: 35.6895, lon: 139.6917, img: 'data:image/jpeg;base64,' }
    ];

    const [err, setError] = useState("")
    const [weather, setWeather] = useState(null)
    const [loading, setLoading] = useState(true)
    const baseUrl = import.meta.env.VITE_WEATHER_API_ENDPOINT

    const fetchApi = async () =>{
        setLoading(true)
        setError(null)
        try{

            const result =  await Promise.all(

                ciudades.map(async ciudad => {
                    
                    const url = `${baseUrl}?latitude=${ciudad.lat}&longitude=${ciudad.lon}&current_weather=true` 
                    
                    const response = await fetch(url)
                    if(!response.ok)
                    {
                        throw new Error(`Error al realizar la consulta HTTP: ${response.status}`)
                    }else{
                        const data = await response.json()
                        return{
                            nombre:ciudad.nombre,
                            img: ciudad.img,
                            clima: data.current_weather
                        }
                    }
                })
            )
            setWeather(result)

        }catch(Error){
            setError(Error)
            setWeather([])
        }finally{
            setLoading(false)
        }
    }
    

    useEffect(() =>{
        fetchApi()
    },[])

    return{
        err,
        weather,
        loading
    }
}
export default useWeatherApi